import React from 'react'

function ItemCount({ stock, initial,  onAdd }) {

    const sumar = ({onAdd})=>{

      setClick({onAdd} + 1) 
    }

    const restar = ({onAdd})=>{

      setClick({onAdd} - 1) 
    }


  return (
    <>
    <div>
      <button onClick={sumar}>+</button> 
      <span >TOTAL {onAdd}</span>
      <button onClick={restar}>-</button> 
    </div>
    </>
  )
}

export default ItemCount
